<canvas bind:this={canvas} {width} {height} style="width:{width}rem;height:{height}rem;"></canvas>

<script>
	export let img = undefined
	export let current = 0
	export let width = 0
	export let height = 0

	let canvas
	let ctx

	import { onMount } from 'svelte'
	onMount(() => {
		ctx = canvas.getContext('2d')
	})

	$: if (ctx && (img || current)) {
		ctx.clearRect(0, 0, width, height)
		ctx.drawImage(img, (current * width), 0, width, height, 0, 0, width, height)
	}
</script>

